async () => {
  await closeModal('mdl_details');
  await storeValue('cart', appsmith.store.cart.concat({
    id: lst_products.selectedItem.id,
    model: lst_products.selectedItem.model,
    qty: 1,
    price: lst_products.selectedItem.price,
    image: lst_products.selectedItem.image
  }));
  await showAlert(`${lst_products.selectedItem.model} added to cart`, 'info');
}

